#include<bits/stdc++.h>
using namespace std;

int main() {
    string s;
    cin >> s;
    int n = s.size();
    int a[26] = {0};
    for (int i = 0; i < n; i++) {
        a[s[i] - 'A']++;
    }
    int vowels = a['A' - 'A'] + a['E' - 'A'] + a['I' - 'A'] + a['O' - 'A'] + a['U' - 'A'] + a['Y' - 'A'];
    int consonants = n - vowels;
    int ng = min(a['N' - 'A'], a['G' - 'A']);
    consonants += ng;
    vowels -= a['Y' - 'A'];
    int syllables = min(consonants / 2, vowels);
    cout << syllables * 3 << endl;
    return 0;
}
