#include<bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    
    int t;
    cin >> t;
    while(t--) {
        int n;
        cin >> n;
        vector<int> a(n), b(n);
        for(int i=0;i<n;i++)
            cin >> a[i];
        for(int i=0;i<n;i++)
            cin >> b[i];
        vector<long long> prefixA(n), prefixB(n), suffixA(n), suffixB(n);
        prefixA[0] = a[0];
        prefixB[0] = b[0];
        for(int i=1;i<n;i++) {
            prefixA[i] = prefixA[i-1] + a[i];
            prefixB[i] = prefixB[i-1] + b[i];
        }
        suffixA[n-1] = a[n-1];
        suffixB[n-1] = b[n-1];
        for(int i=n-2;i>=0;i--) {
            suffixA[i] = suffixA[i+1] + a[i];
            suffixB[i] = suffixB[i+1] + b[i];
        }
        long long ans = max(suffixA[0], suffixB[0]);
        for(int i=0;i<n-1;i++) {
            ans = max(ans, prefixA[i] + suffixB[i+1]);
            ans = max(ans, prefixB[i] + suffixA[i+1]);
        }
        cout << ans << '\n';
    }
    return 0;
}
