        for(int i = 0; i < n; i++) {
            cin >> a[i];
            if(a[i] != -1 && a[i] != 1) x = a[i];
            sum += (a[i] == -1 ? -1 : 1);
        }
        set<int> s;
        s.insert(sum);
        for(int i = -n; i <= n; i++) {
            if(i + x >= -n && i + x <= n) s.insert(i + x);
