#include<bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t;
    cin >> t;
    while(t--) {
        int n;
        cin >> n;
        vector<int> a(n);
        int sum = 0, min_val = 0, max_val = 0;
        for(int i = 0; i < n; i++) {
            cin >> a[i];
            if(a[i] == -1 || a[i] == 1) {
                sum += a[i];
                min_val = min(min_val, sum);
                max_val = max(max_val, sum);
            }
        }
        set<int> s;
        for(int i = min_val; i <= max_val; i++) {
            s.insert(i);
        }
        for(int i = 0; i < n; i++) {
            if(a[i] != -1 && a[i] != 1) {
                for(int j = min_val; j <= max_val; j++) {
                    s.insert(j + a[i]);
                }
            }
        }
        cout << s.size() << "\n";
        for(auto it : s) {
            cout << it << " ";
        }
        cout << "\n";
    }

    return 0;
}
