#include<bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t;
    cin >> t;
    while(t--) {
        int n;
        cin >> n;
        vector<int> a(n);
        int max_val = INT_MIN;
        int min_val = INT_MAX;
        int ones = 0;
        int minus_ones = 0;
        for(int i=0; i<n; i++) {
            cin >> a[i];
            if(a[i] == 1) ones++;
            else if(a[i] == -1) minus_ones++;
            else {
                max_val = max(max_val, a[i]);
                min_val = min(min_val, a[i]);
            }
        }
        set<int> sums;
        for(int i=minus_ones; i<=ones; i++) {
            if(min_val != INT_MAX) {
                sums.insert(min_val + i);
                sums.insert(max_val + i);
            } else {
                sums.insert(i);
            }
        }
        for(auto it: sums) {
            cout << it << " ";
        }
        cout << "\n";
    }

    return 0;
}
