#include<bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t;
    cin >> t;
    while(t--) {
        int n;
        cin >> n;
        vector<int> a(n);
        int x = 0;
        int ones = 0;
        for(int i = 0; i < n; i++) {
            cin >> a[i];
            if(a[i] != -1 && a[i] != 1) {
                x = a[i];
            } else if(a[i] == 1) {
                ones++;
            }
        }

        set<int> sums;
        sums.insert(0);
        for(int i = 1; i <= ones; i++) {
            sums.insert(i);
            sums.insert(-i);
        }

        if(x != 0) {
            vector<int> temp(sums.begin(), sums.end());
            for(int i = 0; i < temp.size(); i++) {
                sums.insert(temp[i] + x);
            }
        }

        for(auto it : sums) {
            cout << it << " ";
        }
        cout << "\n";
    }

    return 0;
}
